import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate replaces useHistory

const AddPrescriptionReminder = () => {
  const [reminder, setReminder] = useState({ medication: '', date: '', time: '', notes: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setReminder({ ...reminder, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Save to local storage
    const savedReminders = JSON.parse(localStorage.getItem('prescriptionReminders')) || [];
    localStorage.setItem('prescriptionReminders', JSON.stringify([...savedReminders, reminder]));
    navigate('/prescription-reminders'); // Redirect after adding
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Add a New Medication Reminder</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          name="medication"
          value={reminder.medication}
          onChange={handleChange}
          placeholder="Medication Name"
          required
          style={styles.input}
        />
        <input
          type="date"
          name="date"
          value={reminder.date}
          onChange={handleChange}
          required
          style={styles.input}
        />
        <input
          type="time"
          name="time"
          value={reminder.time}
          onChange={handleChange}
          required
          style={styles.input}
        />
        <textarea
          name="notes"
          value={reminder.notes}
          onChange={handleChange}
          placeholder="Additional Notes"
          required
          style={styles.textarea}
        />
        <button type="submit" style={styles.button}>Add Reminder</button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#ffe6f2', // Soft pink background
    borderRadius: '10px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    maxWidth: '400px',
    margin: 'auto',
  },
  title: {
    textAlign: 'center',
    color: '#c36', // Cute color for the title
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    margin: '10px 0',
    padding: '10px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    boxShadow: 'inset 0 2px 5px rgba(0, 0, 0, 0.1)',
    fontSize: '16px',
  },
  textarea: {
    margin: '10px 0',
    padding: '10px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    boxShadow: 'inset 0 2px 5px rgba(0, 0, 0, 0.1)',
    fontSize: '16px',
    resize: 'none', // Prevent resizing
    height: '100px', // Fixed height for textarea
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#66cc66', // Soft green background
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '18px',
    transition: 'background-color 0.3s',
  },
};

export default AddPrescriptionReminder;
