// charitySlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  charityDetails: null,
  charities: [],  // Add an array to store all charities
  donors: [],
  totalDonations: 0,
  beneficiaries: [],
  inventory: [],
  stories: [],
};

const charitySlice = createSlice({
  name: 'charity',
  initialState,
  reducers: {
    setCharityDetails: (state, action) => {
      state.charityDetails = action.payload;
    },
    addCharity: (state, action) => {
      state.charities.push(action.payload); // Add new charity to the list
    },
    setDonors: (state, action) => {
      state.donors = action.payload;
    },
    setTotalDonations: (state, action) => {
      state.totalDonations = action.payload;
    },
    setInventory: (state, action) => {
      state.inventory = action.payload;
    },
    addBeneficiary: (state, action) => {
      state.beneficiaries.push(action.payload);
    },
    addStory: (state, action) => {
      state.stories.push(action.payload);
    },
  },
});

export const {
  setCharityDetails,
  addCharity,  // Export the addCharity action
  setDonors,
  setTotalDonations,
  setInventory,
  addBeneficiary,
  addStory,
} = charitySlice.actions;

export default charitySlice.reducer;
